package org.pvgus
package crypto

import java.nio.charset.StandardCharsets
import java.security.{KeyFactory, Signature}
import java.security.spec.PKCS8EncodedKeySpec
import java.util.Base64

object RsaCrypto {

  private val keyAlgorithm = "RSA"
  private val stringKey = "";
  private val signatureType = "SHA256withRSA"

  def sign(string: String): String = {
    val key = KeyFactory.getInstance(keyAlgorithm).generatePrivate(new PKCS8EncodedKeySpec(Base64.getDecoder.decode(stringKey.getBytes(StandardCharsets.UTF_8))))
    val signature = Signature.getInstance(signatureType)
    signature.initSign(key)
    signature.update(string.getBytes)
    new String(Base64.getEncoder.encode(signature.sign))
  };

}
